mapper.select:
SELECT * FROM CATEGORY 

level=#{level}

and cateClass=#{cateClass}

sql.where:
	display ='y' and sellState='y'


	and categoryId like CONCAT(#{cateClass}, '__')


	and categoryId=#{cateId}



and


			(
			b.P_ID = #{searchKey } or
			title Like CONCAT('%', #{searchKey }, '%') 
			)


			title Like CONCAT('%', #{searchKey }, '%') 


			b.P_ID = #{searchKey }

mapper.select:
SELECT b.P_ID as pId,itemId,title, description, translator, author,coverLargeUrl, categoryId, categoryName, publisher, sellCount, stock, saleRate, salePrice,realPrice, regDate, sellState,serviceType,mileage, display, DATE_FORMAT(sellStart, '%Y.%m.%d %h:%m') as sellStart, DATE_FORMAT(sellEnd, '%Y.%m.%d %h:%m') as sellEnd, ROUND(avg(R_STAR),1) as rStar,count(R_ID) as rCount FROM BOOK_GOODS b left join REVIEW r on b.P_ID = r.P_ID
group by b.P_ID
order by ${sort}
LIMIT #{pageIndex}, #{pageSize}

mapper.insert:
INSERT INTO CART
(
	BOOK_NAME, CART_STOCK, CART_PRICE, CART_REG_DT, ORDER_YN, loginID, CART_IMG, MILEAGE, P_ID
)
VALUES
(
	#{title},1,#{realPrice},NOW(),'N',#{loginID},#{coverLargeUrl},#{mileage},#{pId}
)

mapper.select:
SELECT BOOK_NAME AS title
FROM CART
WHERE P_ID = #{pId}
AND ORDER_YN != 'Y'
AND loginID = #{loginID}
