mapper.insert:
        
            SELECT IFNULL(MAX(CAST(std_num AS UNSIGNED)),0) + 1 FROM tb_userinfo 
         	
		insert into tb_userinfo (
		   loginID
		  ,user_type
		  ,name
		  ,password
		  ,tel 
		  ,sex
		  ,birth
		  ,mail
		  ,addr
		  ,join_date
		  ,pid
		  ,std_num
		) values (
		   #{loginID} 
		  ,#{user_type}
		  ,#{name}
		  ,#{password} 
		  ,#{tel}
		  ,#{sex} 
		  ,STR_TO_DATE(#{birth}, '%Y%m%d')  
		  ,#{mail}  
		  ,#{addr}
		  ,now()
		  ,#{pid} 
		  ,#{std_numsel}  
		)

mapper.select:
SELECT  COUNT(loginID) AS che
  FROM  tb_userinfo 
 WHERE  loginID = #{loginID}
