mapper.update:
UPDATE CART
SET CART_STOCK = #{stock}
WHERE CART_NO = #{cartNo}

mapper.insert:
INSERT INTO ORDERS
(
	ORDER_NO
	, PAYMENT
	, ORDER_DT
	, CART_NO
	, ORDER_STATE
	, loginID
	, DC_PRICE
	, COUPON_NO
)
VALUES
(
	#{payNo}
	, #{totalPrice}
	, now()
	, #{cartNos}
	, '0'
	, #{loginID}
	, #{couponPrice}
	, #{couponNos}
)

INSERT INTO ORDER_HST
(
	ORDER_NO
	, ORDER_DT
	, ORDER_STATE
)
VALUES
(
	#{payNo}
	, NOW()
	, #{userState}
)

mapper.update:
UPDATE ORDERS
SET ORDER_STATE = '2'
WHERE ORDER_NO = #{payNo}

UPDATE ORDERS
SET ORDER_STATE = '3'
WHERE ORDER_NO = #{payNo}

UPDATE COUPON_HST
SET COUPON_USE_DT = NOW()
WHERE COUPON_HST_NO = #{couponNo}

mapper.select:
SELECT COUPON_HST_NO AS couponNo
FROM COUPON_HST
WHERE COUPON_USE_DT = #{regDt}
  AND loginID = #{loginID}

mapper.update:
UPDATE COUPON_HST
SET COUPON_USE_DT = '0000-00-00 00:00:00'
WHERE COUPON_HST_NO = #{couponNo}
