mapper.select:
   		select * from MENU 

   			menuType= #{menuType} and menuAuth is null 

   			or menuType= #{menuType} and menuAuth=#{menuAuth}


			and menuHigh= #{menuHigh}


		  	and menuLvl= #{menuLvl}

   		select * from MENU 

   			menuType= 'Admin' and menuAuth = 'A' and menuHigh is null

   			or menuType= #{menuType} and menuAuth=#{menuAuth}


			and menuLvl= #{menuLvl}
