mapper.select:
/*kr.kosmo.jobkorea.login.dao.LoginDao.checkLogin*/
      SELECT  user_Type
        FROM    tb_userinfo 
      WHERE   loginID = #{lgn_Id}
         AND  password = #{pwd}

/*kr.kosmo.jobkorea.login.dao.LoginDao.selectFindPw*/
SELECT  ta.lgn_id
, ta.pwd
, tb.cpn_ctr
, tb.cpn_1
, tb.cpn_2
, tb.cpn_3
, tb.eml
FROM   tm_usr_mst ta
   ,   tn_usr_dtl_info tb
WHERE   ta.usr_sst_id = tb.usr_sst_id
AND   ta.use_poa = 'Y'
AND   ta.dlt_poa = 'N'


      AND ta.lgn_id = #{lgn_id}
      AND   ta.usr_nm = #{usr_nm}
      AND tb.cpn_ctr = #{cpn_ctr}
      AND tb.cpn_1 = #{cpn_1}
      AND tb.cpn_2 = #{cpn_2}
      AND tb.cpn_3 = #{cpn_3}


      AND ta.lgn_id = #{lgn_id}
      AND   ta.usr_nm = #{usr_nm}
      AND tb.eml = #{eml}


      AND 1=2

SELECT loginID 
  FROM tb_userinfo
 WHERE M_EMAIL =  #{mail}
 AND NAME = #{NAME}

SELECT mail, password
  FROM tb_userinfo 
 WHERE loginID = #{loginID}

mapper.update:
UPDATE tb_userinfo
   SET password = #{pwd}
 WHERE loginID  = #{loginID}
